// ReactChartsLine.js
import React, { useState, useEffect } from 'react';
import {
    LineChart,
    Line,
    XAxis,
    YAxis,
    CartesianGrid,
    Tooltip,
    Legend,
    ResponsiveContainer,
} from 'recharts';

function LineGraph() {
    const [data, setData] = useState([]);

    useEffect(() => {
        // Use the environment variable for API URL if available,
        // otherwise default to localhost (good for development).
        const apiUrl = process.env.REACT_APP_API_URL || 'http://localhost:8000';
        const fetchUrl = `${apiUrl}/sensorData/last12hours`;

        const fetchSensorData = async () => {
            try {
                const response = await fetch(fetchUrl);
                if (!response.ok) {
                    throw new Error(`HTTP error! status: ${response.status}`);
                }
                const result = await response.json();
                console.log("Fetched sensor data:", result);

                // Sort the data by the utc timestamp (oldest first)
                const sortedData = result.sort(
                    (a, b) => new Date(a.utc) - new Date(b.utc)
                );
                console.log("Sorted sensor data:", sortedData);

                // Map the data to the format needed by Recharts
                const chartData = sortedData.map((item) => ({
                    utc: item.utc,
                    reading: parseFloat(item.reading),
                }));
                console.log("Chart data:", chartData);

                setData(chartData);
            } catch (error) {
                console.error("Error fetching sensor data:", error);
            }
        };

        fetchSensorData();
    }, []);


    const exdata = [
        {
            name: 'Page A',
            uv: 4000,
            pv: 2400,
            amt: 2400,
        },
        {
            name: 'Page B',
            uv: 3000,
            pv: 1398,
            amt: 2210,
        },
        {
            name: 'Page C',
            uv: 2000,
            pv: 9800,
            amt: 2290,
        },
        {
            name: 'Page D',
            uv: 2780,
            pv: 3908,
            amt: 2000,
        },
        {
            name: 'Page E',
            uv: 1890,
            pv: 4800,
            amt: 2181,
        },
        {
            name: 'Page F',
            uv: 2390,
            pv: 3800,
            amt: 2500,
        },
        {
            name: 'Page G',
            uv: 3490,
            pv: 4300,
            amt: 2100,
        },
    ];

    return (
        // Ensure that the container has an explicit height.
        <div style={{ width: '100%', height: 400 }}>
            <ResponsiveContainer width="100%" height="100%">
                <LineChart
                    width={500}
                    height={300}
                    data={exdata}
                    margin={{
                        top: 5,
                        right: 30,
                        left: 20,
                        bottom: 5,
                    }}
                >
                    <CartesianGrid strokeDasharray="3 3" />
                    <XAxis dataKey="name" />
                    <YAxis />
                    <Tooltip />
                    <Legend />
                    <Line type="monotone" dataKey="pv" stroke="#8884d8" activeDot={{ r: 8 }} />
                    <Line type="monotone" dataKey="uv" stroke="#82ca9d" />
                </LineChart>
            </ResponsiveContainer>
        </div>
    );
}

export default LineGraph;
