import React, { useState, useEffect } from 'react';
import {
    LineChart, Line, XAxis, YAxis,
    CartesianGrid, Tooltip, Legend,
    ResponsiveContainer,
} from 'recharts';
import './ReactChartsLine.css';

const CustomTooltip = ({ active, payload, label }) => {
    if (!active || !payload?.length) return null;
    const ts = typeof label === 'number' ? label : Number(label);
    if (!isFinite(ts)) return null;
    const fmt = new Intl.DateTimeFormat('en-US', {
        hour12: false, month: '2-digit', day: '2-digit',
        hour: '2-digit', minute: '2-digit',
    });
    return (
        <div style={{ background:'#fff', border:'1px solid #ccc', padding:8 }}>
            <div style={{ fontWeight:'bold' }}>Time: {fmt.format(ts)}</div>
            {payload.map(e => (
                <div key={e.dataKey} style={{ color:e.color }}>
                    <strong>{e.name}:</strong> {e.value}
                </div>
            ))}
        </div>
    );
};

export default function LineGraph() {
    const [data, setData]         = useState([]);
    const [rawData, setRawData]   = useState([]);
    const [selected, setSelected] = useState([]);
    const [inited, setInited]     = useState(false);

    const [startDate, setStartDate] = useState('');
    const [startTime, setStartTime] = useState('');
    const [endDate,   setEndDate]   = useState('');
    const [endTime,   setEndTime]   = useState('');

    const [yMin, setYMin]   = useState('0');
    const [yMax, setYMax]   = useState('0');
    const [yStep, setYStep] = useState(5);

    const api = process.env.REACT_APP_API_URL || 'http://localhost:8000';

    // on‑mount: round to 5min & set filters
    useEffect(() => {
        const pad = n => String(n).padStart(2,'0');
        const round5 = d => {
            const ms = d.getTime(), step = 5*60*1000;
            return new Date(Math.floor(ms/step)*step);
        };
        const now = round5(new Date());
        const ago12 = new Date(now.getTime() - 12*3600*1000);

        setStartDate(`${ago12.getFullYear()}-${pad(ago12.getMonth()+1)}-${pad(ago12.getDate())}`);
        setStartTime(`${pad(ago12.getHours())}:${pad(ago12.getMinutes())}`);
        setEndDate(`${now.getFullYear()}-${pad(now.getMonth()+1)}-${pad(now.getDate())}`);
        setEndTime(`${pad(now.getHours())}:${pad(now.getMinutes())}`);

        setInited(true);
    }, []);

    // fetch & pivot, auto‑select any IDs we see
    useEffect(() => {
        if (!inited) return;
        const doFetch = () => {
            const p = new URLSearchParams();
            if (startDate) p.set('startDate', startDate);
            if (startTime) p.set('startTime', startTime);
            if (endDate)   p.set('endDate', endDate);
            if (endTime)   p.set('endTime', endTime);

            fetch(`${api}/sensorData/filter?${p}`)
                .then(r => r.json())
                .then(rows => {
                    setRawData(rows);

                    // auto‑detect IDs
                    const ids = Array.from(new Set(rows.map(r => r.sensor_id)));
                    setSelected(ids);

                    // pivot
                    const map = {};
                    rows.forEach(({ sensor_id, local, reading }) => {
                        const ts = new Date(local).getTime();
                        map[ts] = map[ts] || { timestamp: ts };
                        map[ts][sensor_id] = parseFloat(reading);
                    });
                    const arr = Object.values(map).sort((a,b)=>a.timestamp-b.timestamp);
                    setData(arr);

                    // recompute Y
                    const all = rows.map(r => parseFloat(r.reading));
                    const mx = all.length ? Math.max(...all) : 0;
                    const ceil = Math.ceil(mx / yStep) * yStep;
                    setYMin('0'); setYMax(String(ceil));
                })
                .catch(console.error);
        };

        doFetch();
        const iv = setInterval(doFetch, 60_000);
        return () => clearInterval(iv);
    }, [api, inited, startDate, startTime, endDate, endTime, yStep]);

    // ticks
    const sTs = new Date(`${startDate}T${startTime}`).getTime();
    const eTs = new Date(`${endDate}T${endTime}`).getTime();
    const xTicks = [], yTicks = [];
    let ft = new Date(sTs); ft.setMinutes(0,0,0);
    if (ft < sTs) ft = new Date(ft.getTime()+3600_000);
    for (let t=ft.getTime(); t<=eTs; t+=3600_000) xTicks.push(t);
    const yMinN = +yMin, yMaxN = +yMax;
    for (let v=yMinN; v<=yMaxN; v+=yStep) yTicks.push(v);

    const colors = ['#8884d8','#82ca9d','#ffc658','#ff7300','#a4de6c'];

    return (
        <div style={{ width:'100%', height:600 }}>
            {/* filters */}
            <div className="menuContainer">
                <section className="section2">
                    <h4>Date & Range</h4>
                    <div style={{ display:'flex', gap:24 }}>
                        <div>
                            <label>Start Date<br/>
                                <input type="date" value={startDate} onChange={e=>setStartDate(e.target.value)} />
                            </label><br/>
                            <label>Start Time<br/>
                                <input type="time" step="300" value={startTime}
                                       onChange={e=>setStartTime(e.target.value)} />
                            </label>
                        </div>
                        <div>
                            <label>End Date<br/>
                                <input type="date" value={endDate} onChange={e=>setEndDate(e.target.value)} />
                            </label><br/>
                            <label>End Time<br/>
                                <input type="time" step="300" value={endTime}
                                       onChange={e=>setEndTime(e.target.value)} />
                            </label>
                        </div>
                    </div>
                </section>
            </div>

            {/* chart */}
            <ResponsiveContainer>
                <LineChart data={data} margin={{ top:20,right:10,bottom:80,left:10 }}>
                    <CartesianGrid strokeDasharray="3 3"/>
                    <XAxis
                        dataKey="timestamp" type="number" domain={[sTs,eTs]} ticks={xTicks}
                        tickFormatter={ms=>new Date(ms).toLocaleTimeString('en-GB',{hour12:false,hour:'2-digit',minute:'2-digit'})}
                        label={{ value:'Local Time', position:'bottom', offset:20, style:{fontWeight:'bold'} }}
                    />
                    <YAxis
                        domain={[yMinN,yMaxN]} ticks={yTicks}
                        label={{ value:'mag/arcsec²', angle:-90, position:'insideLeft', dy:50, style:{fontWeight:'bold'} }}
                    />
                    <Tooltip content={<CustomTooltip/>}/>
                    <Legend verticalAlign="top"/>
                    {selected.map((id,i) => (
                        <Line
                            key={id} type="monotone"
                            dataKey={id}
                            name={id}
                            stroke={colors[i%colors.length]}
                            dot={false}
                        />
                    ))}
                </LineChart>
            </ResponsiveContainer>
        </div>
    );
}
