import React, { useState, useEffect } from 'react';
import {
    LineChart,
    Line,
    XAxis,
    YAxis,
    CartesianGrid,
    Tooltip,
    Legend,
    ResponsiveContainer,
} from 'recharts';
import './ReactChartsLine.css';

// Custom tooltip to display multiple sensor readings with formatted time
const CustomTooltip = ({ active, payload, label }) => {
    if (!active || !payload || payload.length === 0) return null;
    const timeLabel = new Date(label).toLocaleTimeString('en-GB', {
        hour12: false,
        hour: '2-digit',
        minute: '2-digit',
        second: '2-digit',
    });
    return (
        <div style={{ backgroundColor: '#fff', border: '1px solid #ccc', padding: '0.5rem', color: '#000' }}>
            <div style={{ fontWeight: 'bold', marginBottom: 4 }}>Time: {timeLabel}</div>
            {payload.map((entry) => (
                <div key={entry.dataKey} style={{ color: entry.color, fontWeight: 'bold' }}>
                    {entry.name}: {entry.value}
                </div>
            ))}
        </div>
    );
};

function LineGraph() {
    // Chart data and full raw rows
    const [data, setData] = useState([]);       // grouped for chart
    const [rawData, setRawData] = useState([]); // full DB rows for CSV
    const [sensors, setSensors] = useState([]);
    const [selectedSensors, setSelectedSensors] = useState([]);
    const [initialized, setInitialized] = useState(false);

    // Date/time filters
    const [startDate, setStartDate] = useState('');
    const [startTime, setStartTime] = useState('');
    const [endDate, setEndDate] = useState('');
    const [endTime, setEndTime] = useState('');

    // Y‑axis customization
    const [yMin, setYMin] = useState('');
    const [yMax, setYMax] = useState('');
    const [yStep, setYStep] = useState(5);

    const apiUrl = process.env.REACT_APP_API_URL || 'http://localhost:8000';

    // Toggle sensor selection (enforce at least one)
    const toggleSensor = (sensorId) => {
        setSelectedSensors(prev => {
            if (prev.includes(sensorId)) {
                if (prev.length === 1) return prev;
                return prev.filter(id => id !== sensorId);
            }
            return [...prev, sensorId];
        });
    };

    // Download CSV of rawData (all DB columns)
    const downloadCSV = () => {
        if (!rawData.length) return;
        const headers = Object.keys(rawData[0]);
        const rows = rawData.map(row => headers.map(h => row[h]));
        const csvContent = [
            headers.join(','),
            ...rows.map(r => r.map(cell => `"${cell}"`).join(','))
        ].join('\r\n');
        const blob = new Blob([csvContent], { type: 'text/csv' });
        const url = URL.createObjectURL(blob);
        const a = document.createElement('a');
        a.href = url;
        a.download = 'Utah-skyscope-data.csv';
        document.body.appendChild(a);
        a.click();
        document.body.removeChild(a);
        URL.revokeObjectURL(url);
    };

    // On mount: fetch sensors & set default filters (rounded to 5 min)
    useEffect(() => {
        const pad = n => String(n).padStart(2, '0');
        const round5 = date => {
            const ms = date.getTime(), step = 1000 * 60 * 5;
            return new Date(Math.floor(ms / step) * step);
        };
        const now = round5(new Date());
        const ago12h = new Date(now.getTime() - 12 * 3600 * 1000);

        setStartDate(`${ago12h.getFullYear()}-${pad(ago12h.getMonth()+1)}-${pad(ago12h.getDate())}`);
        setStartTime(`${pad(ago12h.getHours())}:${pad(ago12h.getMinutes())}`);
        setEndDate(`${now.getFullYear()}-${pad(now.getMonth()+1)}-${pad(now.getDate())}`);
        setEndTime(`${pad(now.getHours())}:${pad(now.getMinutes())}`);

        fetch(`${apiUrl}/sensors`)
            .then(r => r.json())
            .then(list => {
                setSensors(list);
                setSelectedSensors(list.map(s => s.sensor_id));
                setInitialized(true);
            })
            .catch(console.error);
    }, [apiUrl]);

    // Polling fetch: get rawData, group for chart, recalc Y‑axis
    useEffect(() => {
        if (!initialized) return;
        const fetchAndProcess = () => {
            const params = new URLSearchParams();
            if (selectedSensors.length) params.append('sensor', selectedSensors.join(','));
            if (startDate) params.append('startDate', startDate);
            if (startTime) params.append('startTime', startTime);
            if (endDate) params.append('endDate', endDate);
            if (endTime) params.append('endTime', endTime);

            fetch(`${apiUrl}/sensorData/filter?${params.toString()}`)
                .then(r => r.json())
                .then(rows => {
                    setRawData(rows);
                    const grouped = {};
                    const readings = [];
                    rows.forEach(item => {
                        const ts = new Date(item.local).getTime();
                        grouped[ts] = grouped[ts] || { timestamp: ts };
                        grouped[ts][item.sensor_id] = parseFloat(item.reading);
                        readings.push(parseFloat(item.reading));
                    });
                    const arr = Object.values(grouped);
                    setData(arr);
                    const maxRead = readings.length ? Math.max(...readings) : 0;
                    const ceilMax = Math.ceil(maxRead / yStep) * yStep;
                    setYMin('0');
                    setYMax(ceilMax.toString());
                })
                .catch(console.error);
        };

        fetchAndProcess();
        const id = setInterval(fetchAndProcess, 60000);
        return () => clearInterval(id);
    }, [apiUrl, initialized, startDate, startTime, endDate, endTime, selectedSensors, yStep]);

    // Start/end timestamps
    const startTs = startDate && startTime
        ? new Date(`${startDate}T${startTime}`).getTime()
        : 0;
    const endTs = endDate && endTime
        ? new Date(`${endDate}T${endTime}`).getTime()
        : Date.now();

    // Generate X ticks on exact hours
    const xTicks = [];
    if (startTs < endTs) {
        let first = new Date(startTs);
        first.setMinutes(0, 0, 0);
        if (first.getTime() < startTs) {
            first = new Date(first.getTime() + 3600 * 1000);
        }
        for (let t = first.getTime(); t <= endTs; t += 3600 * 1000) {
            xTicks.push(t);
        }
    }

    // Generate Y ticks by yStep
    const yMinNum = Number(yMin || 0);
    const yMaxNum = Number(yMax || 0);
    const yTicks = [];
    for (let v = yMinNum; v <= yMaxNum; v += yStep) {
        yTicks.push(v);
    }

    const colors = ['#8884d8', '#82ca9d', '#ffc658', '#ff7300', '#a4de6c'];

    return (
        <div style={{ width: '100%', height: 600 }}>
            <div className="menuContainer">
                {/* Section 1: Sensor Selection */}
                <section className="section1">
                    <h4>Sensor Selection</h4>
                    <div className="sensor-options">
                        {sensors.map(s => (
                            <label key={s.sensor_id} style={{ marginRight: 12 }}>
                                <input
                                    type="checkbox"
                                    checked={selectedSensors.includes(s.sensor_id)}
                                    onChange={() => toggleSensor(s.sensor_id)}
                                />
                                {s.name}
                            </label>
                        ))}
                    </div>
                </section>

                {/* Section 2: Date & Range */}
                <section className="section2">
                    <h4>Date and Range</h4>
                    <div className="range-selector" style={{ display: 'flex', gap: 24, justifyContent: 'center' }}>
                        <div>
                            <label>Start Date<br/>
                                <input type="date" value={startDate} onChange={e => setStartDate(e.target.value)} />
                            </label><br/>
                            <label>Start Time<br/>
                                <input type="time" step="300" value={startTime} onChange={e => setStartTime(e.target.value)} />
                            </label>
                        </div>
                        <div>
                            <label>End Date<br/>
                                <input type="date" value={endDate} onChange={e => setEndDate(e.target.value)} />
                            </label><br/>
                            <label>End Time<br/>
                                <input type="time" step="300" value={endTime} onChange={e => setEndTime(e.target.value)} />
                            </label>
                        </div>
                    </div>
                </section>

                {/* Section 3: Y‑Axis & CSV */}
                <section className="section3">
                    <h4>Data Options & CSV</h4>
                    <div style={{ display: 'flex', alignItems: 'center', gap: 24, marginBottom: 8 }}>
                        <div>
                            <label>Y Min<br/>
                                <input type="number" value={yMin} onChange={e => setYMin(e.target.value)} style={{ width: 60 }} />
                            </label>
                        </div>
                        <div>
                            <label>Y Max<br/>
                                <input type="number" value={yMax} onChange={e => setYMax(e.target.value)} style={{ width: 60 }} />
                            </label>
                        </div>
                        <div>
                            <label>Y Step<br/>
                                <select value={yStep} onChange={e => setYStep(Number(e.target.value))}>
                                    <option value={1}>1 unit</option>
                                    <option value={2}>2 units</option>
                                    <option value={5}>5 units</option>
                                    <option value={10}>10 units</option>
                                </select>
                            </label>
                        </div>
                        <button onClick={downloadCSV}>Download as CSV</button>
                    </div>
                </section>
            </div>

            {/* Chart */}
            <ResponsiveContainer width="100%" height="100%">
                <LineChart data={data} margin={{ top: 20, right: 10, bottom: 80, left: 10 }}>
                    <CartesianGrid strokeDasharray="3 3" />
                    <XAxis
                        dataKey="timestamp"
                        type="number"
                        domain={[startTs, endTs]}
                        ticks={xTicks}
                        tickFormatter={ms =>
                            new Date(ms).toLocaleTimeString('en-GB', {
                                hour12: false,
                                hour: '2-digit',
                                minute: '2-digit',
                                second: '2-digit',
                            })
                        }
                        label={{ value: 'Local Time', position: 'bottom', offset: 20, style: { fontWeight: 'bold' } }}
                    />
                    <YAxis
                        domain={[yMinNum, yMaxNum]}
                        ticks={yTicks}
                        label={{ value: 'mag/arcsec²', angle: -90, position: 'insideLeft', dy: 50, style: { fontWeight: 'bold' } }}
                    />
                    <Tooltip content={<CustomTooltip />} />
                    <Legend verticalAlign="top" />
                    {sensors
                        .filter(s => selectedSensors.includes(s.sensor_id))
                        .map((s, i) => (
                            <Line
                                key={s.sensor_id}
                                type="monotone"
                                dataKey={s.sensor_id}
                                name={s.name}
                                stroke={colors[i % colors.length]}
                                dot={false}
                            />
                        ))}
                </LineChart>
            </ResponsiveContainer>
        </div>
    );
}

export default LineGraph;
