import React, { useState, useEffect } from 'react';
import {
    LineChart,
    Line,
    XAxis,
    YAxis,
    CartesianGrid,
    Tooltip,
    Legend,
    ResponsiveContainer,
} from 'recharts';
import './ReactChartsLine.css';

/* ---------- Tooltip ---------- */
const CustomTooltip = ({ active, payload, label }) => {
    if (!active || !payload?.length) return null;
    const timeLabel = new Date(label).toLocaleTimeString('en-GB', {
        hour12: false, hour: '2-digit', minute: '2-digit', second: '2-digit',
    });
    return (
        <div style={{ backgroundColor: '#fff', border: '1px solid #ccc', padding: '0.5rem' }}>
            <div style={{ fontWeight: 'bold', marginBottom: 4 }}>Time: {timeLabel}</div>
            {payload.map(e => (
                <div key={e.dataKey} style={{ color: e.color, fontWeight: 'bold' }}>
                    {e.name}: {e.value}
                </div>
            ))}
        </div>
    );
};

/* ---------- Main component ---------- */
function LineGraph() {
    /* ---- state ---- */
    const [data, setData]           = useState([]);
    const [rawData, setRawData]     = useState([]);
    const [sensors, setSensors]     = useState([]);
    const [selectedSensors, setSelected] = useState([]);
    const [initialized, setInit]    = useState(false);

    const [startDate, setStartDate] = useState('');
    const [startTime, setStartTime] = useState('');
    const [endDate,   setEndDate]   = useState('');
    const [endTime,   setEndTime]   = useState('');

    const [yMin,  setYMin]  = useState('');
    const [yMax,  setYMax]  = useState('');
    const [yStep, setYStep] = useState(5);

    const apiUrl = process.env.REACT_APP_API_URL || 'http://localhost:8000';

    /* ---- helpers ---- */
    const MS_HOUR = 3600 * 1000;
    const MS_DAY  = 24   * MS_HOUR;

    const toggleSensor = id => {
        setSelected(prev =>
            prev.includes(id)
                ? prev.length > 1 ? prev.filter(s => s !== id) : prev
                : [...prev, id]
        );
    };

    const downloadCSV = () => {
        if (!rawData.length) return;
        const headers = Object.keys(rawData[0]);
        const csv = [
            headers.join(','),
            ...rawData.map(r => headers.map(h => `"${r[h]}"`).join(',')),
        ].join('\r\n');
        const url = URL.createObjectURL(new Blob([csv], { type: 'text/csv' }));
        const a = document.createElement('a');
        a.href = url; a.download = 'Utah-skyscope-data.csv';
        document.body.appendChild(a); a.click(); a.remove();
        URL.revokeObjectURL(url);
    };

    /* ---- initial load ---- */
    useEffect(() => {
        const pad = n => String(n).padStart(2, '0');
        const round5 = d => new Date(Math.floor(d.getTime() / (5*60*1000)) * (5*60*1000));
        const now    = round5(new Date());
        const ago12h = new Date(now.getTime() - 12*MS_HOUR);

        setStartDate(`${ago12h.getFullYear()}-${pad(ago12h.getMonth()+1)}-${pad(ago12h.getDate())}`);
        setStartTime(`${pad(ago12h.getHours())}:${pad(ago12h.getMinutes())}`);
        setEndDate(`${now.getFullYear()}-${pad(now.getMonth()+1)}-${pad(now.getDate())}`);
        setEndTime(`${pad(now.getHours())}:${pad(now.getMinutes())}`);

        fetch(`${apiUrl}/sensors`)
            .then(r => r.json())
            .then(list => { setSensors(list); setSelected(list.map(s => s.sensor_id)); setInit(true); })
            .catch(console.error);
    }, [apiUrl]);

    /* ---- polling fetch ---- */
    useEffect(() => {
        if (!initialized) return;
        const pull = () => {
            const p = new URLSearchParams();
            if (selectedSensors.length) p.append('sensor', selectedSensors.join(','));
            if (startDate) p.append('startDate', startDate);
            if (startTime) p.append('startTime', startTime);
            if (endDate)   p.append('endDate',   endDate);
            if (endTime)   p.append('endTime',   endTime);

            fetch(`${apiUrl}/sensorData/filter?${p.toString()}`)
                .then(r => r.json())
                .then(rows => {
                    setRawData(rows);
                    const grouped = {}, readings = [];
                    rows.forEach(row => {
                        const ts = new Date(row.local).getTime();
                        grouped[ts] ??= { timestamp: ts };
                        grouped[ts][row.sensor_id] = +row.reading;
                        readings.push(+row.reading);
                    });
                    setData(Object.values(grouped));

                    const ceilMax = Math.ceil(Math.max(0, ...readings) / yStep) * yStep;
                    setYMin('0'); setYMax(String(ceilMax));
                })
                .catch(console.error);
        };
        pull();
        const id = setInterval(pull, 60000);
        return () => clearInterval(id);
    }, [apiUrl, initialized, startDate, startTime, endDate, endTime, selectedSensors, yStep]);

    /* ---- axis calculations ---- */
    const startTs = startDate && startTime ? Date.parse(`${startDate}T${startTime}`) : 0;
    const endTs   = endDate   && endTime   ? Date.parse(`${endDate}T${endTime}`)   : Date.now();

    const spanMs  = endTs - startTs;
    const useDays = spanMs > MS_DAY;

    /* --- X ticks --- */
    const xTicks = [];
    if (startTs < endTs) {
        let first = new Date(startTs);
        if (useDays) {
            first.setHours(0, 0, 0, 0);
            if (first.getTime() < startTs) first = new Date(first.getTime() + MS_DAY);
            for (let t = first.getTime(); t <= endTs; t += MS_DAY) xTicks.push(t);
        } else {
            first.setMinutes(0, 0, 0);
            if (first.getTime() < startTs) first = new Date(first.getTime() + MS_HOUR);
            for (let t = first.getTime(); t <= endTs; t += MS_HOUR) xTicks.push(t);
        }
    }

    /* --- Y ticks --- */
    const yMinNum = +yMin || 0, yMaxNum = +yMax || 0;
    const yTicks  = []; for (let v=yMinNum; v<=yMaxNum; v+=yStep) yTicks.push(v);

    const colors = ['#8884d8','#82ca9d','#ffc658','#ff7300','#a4de6c'];

    /* ---------- render ---------- */
    return (
        <div style={{ width: '100%', height: 600 }}>
            {/* … menu sections unchanged … */}
            <div className="menuContainer">
                {/* Sensor selection, date/range, Y‑axis controls (omitted for brevity)… */}
                {/* CSV button */}
                <section className="section3">
                    <h4>Data Options</h4>
                    {/* Y-axis row ... */}
                    <button onClick={downloadCSV}>Download as CSV</button>
                </section>
            </div>

            {/* Chart */}
            <ResponsiveContainer width="100%" height="100%">
                <LineChart data={data} margin={{ top:20, right:10, bottom:80, left:10 }}>
                    <CartesianGrid strokeDasharray="3 3" />
                    <XAxis
                        dataKey="timestamp"
                        type="number"
                        domain={[startTs, endTs]}
                        ticks={xTicks}
                        tickFormatter={ms => new Date(ms).toLocaleString('en-GB', useDays
                            ? { month:'2-digit', day:'2-digit' }                // MM/DD
                            : { hour12:false, hour:'2-digit', minute:'2-digit' } // HH:MM
                        )}
                        label={{
                            value: useDays ? 'Date' : 'Local Time',
                            position: 'bottom',
                            offset: 20,
                            style: { fontWeight: 'bold' }
                        }}
                    />
                    <YAxis
                        domain={[yMinNum, yMaxNum]}
                        ticks={yTicks}
                        label={{ value:'mag/arcsec²', angle:-90, position:'insideLeft', dy:50, style:{fontWeight:'bold'} }}
                    />
                    <Tooltip content={<CustomTooltip/>}/>
                    <Legend verticalAlign="top"/>
                    {sensors.filter(s=>selectedSensors.includes(s.sensor_id))
                        .map((s,i)=>(
                            <Line
                                key={s.sensor_id}
                                type="monotone"
                                dataKey={s.sensor_id}
                                name={s.name}
                                stroke={colors[i%colors.length]}
                                dot={false}
                            />
                        ))}
                </LineChart>
            </ResponsiveContainer>
        </div>
    );
}

export default LineGraph;
