import React, { useState, useEffect } from 'react';
import {
    LineChart,
    Line,
    XAxis,
    YAxis,
    CartesianGrid,
    Tooltip,
    Legend,
    ResponsiveContainer,
} from 'recharts';
import './ReactChartsLine.css';

// Custom tooltip to display multiple sensor readings
const CustomTooltip = ({ active, payload, label }) => {
    if (!active || !payload || payload.length === 0) return null;
    return (
        <div style={{ backgroundColor: '#fff', border: '1px solid #ccc', padding: '0.5rem', color: '#000' }}>
            <div style={{ fontWeight: 'bold', marginBottom: 4 }}>Time: {label}</div>
            {payload.map((entry) => (
                <div key={entry.dataKey} style={{ color: entry.color, fontWeight: 'bold' }}>
                    {entry.name}: {entry.value}
                </div>
            ))}
        </div>
    );
};

function LineGraph() {
    const [data, setData] = useState([]);
    const [selectedSensors, setSelectedSensors] = useState([]);
    const [sensors, setSensors] = useState([]);
    const [initialized, setInitialized] = useState(false);
    const apiUrl = process.env.REACT_APP_API_URL || 'http://localhost:8000';

    const toggleSensor = (sensorId) => {
        setSelectedSensors((prev) => {
            if (prev.includes(sensorId)) {
                if (prev.length === 1) return prev;
                return prev.filter((id) => id !== sensorId);
            }
            return [...prev, sensorId];
        });
    };

    const downloadCSV = () => console.log("CSV download initiated");

    // Fetch sensors and set defaults
    useEffect(() => {
        const now = new Date();
        const end = now;
        const start = new Date(now.getTime() - 12 * 3600 * 1000);
        const pad = (n) => String(n).padStart(2, '0');
        setEndDate(`${end.getFullYear()}-${pad(end.getMonth()+1)}-${pad(end.getDate())}`);
        setEndTime(`${pad(end.getHours())}:${pad(end.getMinutes())}`);
        setStartDate(`${start.getFullYear()}-${pad(start.getMonth()+1)}-${pad(start.getDate())}`);
        setStartTime(`${pad(start.getHours())}:${pad(start.getMinutes())}`);

        fetch(`${apiUrl}/sensors`)
            .then((res) => res.json())
            .then((list) => {
                setSensors(list);
                setSelectedSensors(list.map((s) => s.sensor_id));
                setInitialized(true);
            })
            .catch(console.error);
    }, [apiUrl]);

    // Update graph
    useEffect(() => {
        if (!initialized) return;
        const params = new URLSearchParams();
        if (selectedSensors.length) params.append('sensor', selectedSensors.join(','));
        if (startDate) params.append('startDate', startDate);
        if (startTime) params.append('startTime', startTime);
        if (endDate) params.append('endDate', endDate);
        if (endTime) params.append('endTime', endTime);

        fetch(`${apiUrl}/sensorData/filter?${params.toString()}`)
            .then((res) => res.json())
            .then((result) => {
                // group by formatted time
                const grouped = {};
                result.forEach((item) => {
                    const timeStr = new Date(item.local).toLocaleString('en-GB', {
                        hour12: false,
                        hour: '2-digit',
                        minute: '2-digit',
                        second: '2-digit',
                    });
                    if (!grouped[timeStr]) grouped[timeStr] = { time: timeStr };
                    grouped[timeStr][item.sensor_id] = parseFloat(item.reading);
                });
                setData(Object.values(grouped));
            })
            .catch(console.error);
    }, [startDate, startTime, endDate, endTime, selectedSensors, initialized, apiUrl]);

    // Render
    const colors = ['#8884d8', '#82ca9d', '#ffc658', '#ff7300', '#a4de6c'];
    return (
        <div style={{ width: '100%', height: 500 }}>
            <div className="menuContainer">
                {/* sensor selection omitted for brevity (same as before) */}
            </div>
            <ResponsiveContainer width="100%" height="100%">
                <LineChart data={data} margin={{ top: 20, right: 20, bottom: 80, left: 60 }}>
                    <CartesianGrid strokeDasharray="3 3" />
                    <XAxis dataKey="time" label={{ value: 'Local Time', position: 'bottom', offset: 20, style: { fontWeight: 'bold' } }} />
                    <YAxis label={{ value: 'mag/arcsec²', angle: -90, position: 'insideLeft', dy: 50, style: { fontWeight: 'bold' } }} />
                    <Tooltip content={<CustomTooltip />} />
                    <Legend verticalAlign="top" />
                    {sensors
                        .filter((s) => selectedSensors.includes(s.sensor_id))
                        .map((sensor, idx) => (
                            <Line
                                key={sensor.sensor_id}
                                type="monotone"
                                dataKey={sensor.sensor_id}
                                name={sensor.name}
                                stroke={colors[idx % colors.length]}
                                dot={false}
                            />
                        ))}
                </LineChart>
            </ResponsiveContainer>
        </div>
    );
}

// Extracted local state setters for date/time defaults
const [startDate, setStartDate] = useState('');
const [startTime, setStartTime] = useState('');
const [endDate, setEndDate] = useState('');
const [endTime, setEndTime] = useState('');

export default LineGraph;
