import React, { useState, useEffect } from 'react';
import {
    LineChart,
    Line,
    XAxis,
    YAxis,
    CartesianGrid,
    Tooltip,
    Legend,
    ResponsiveContainer,
} from 'recharts';
import './ReactChartsLine.css';

// Custom tooltip to display multiple sensor readings with formatted time
const CustomTooltip = ({ active, payload, label }) => {
    if (!active || !payload || payload.length === 0) return null;
    const timeLabel = new Date(label).toLocaleTimeString('en-GB', {
        hour12: false,
        hour: '2-digit',
        minute: '2-digit',
        second: '2-digit',
    });
    return (
        <div style={{ backgroundColor: '#fff', border: '1px solid #ccc', padding: '0.5rem', color: '#000' }}>
            <div style={{ fontWeight: 'bold', marginBottom: 4 }}>Time: {timeLabel}</div>
            {payload.map((entry) => (
                <div key={entry.dataKey} style={{ color: entry.color, fontWeight: 'bold' }}>
                    {entry.name}: {entry.value}
                </div>
            ))}
        </div>
    );
};

function LineGraph() {
    // Core state
    const [data, setData] = useState([]);
    const [sensors, setSensors] = useState([]);
    const [selectedSensors, setSelectedSensors] = useState([]);
    const [initialized, setInitialized] = useState(false);

    // Date/time filters (local)
    const [startDate, setStartDate] = useState('');
    const [startTime, setStartTime] = useState('');
    const [endDate, setEndDate] = useState('');
    const [endTime, setEndTime] = useState('');

    // Axis domain customizations
    const [xMinStr, setXMinStr] = useState('');
    const [xMaxStr, setXMaxStr] = useState('');
    const [yMin, setYMin] = useState('');
    const [yMax, setYMax] = useState('');

    const apiUrl = process.env.REACT_APP_API_URL || 'http://localhost:8000';

    // Toggle sensor selection (at least one)
    const toggleSensor = (sensorId) => {
        setSelectedSensors((prev) => {
            if (prev.includes(sensorId)) {
                if (prev.length === 1) return prev;
                return prev.filter((id) => id !== sensorId);
            }
            return [...prev, sensorId];
        });
    };

    // Placeholder for CSV download
    const downloadCSV = () => console.log('Download CSV placeholder');

    // On mount: fetch sensors & set default filters rounded to nearest 5 minutes
    useEffect(() => {
        const pad = (n) => String(n).padStart(2, '0');
        const roundToNearest5Min = (date) => {
            const ms = date.getTime();
            const step = 1000 * 60 * 5;
            return new Date(Math.floor(ms / step) * step);
        };

        const now = new Date();
        const roundedNow = roundToNearest5Min(now);
        const startDateObj = new Date(roundedNow.getTime() - 12 * 3600 * 1000);
        const roundedStart = roundToNearest5Min(startDateObj);

        setStartDate(`${roundedStart.getFullYear()}-${pad(roundedStart.getMonth() + 1)}-${pad(roundedStart.getDate())}`);
        setStartTime(`${pad(roundedStart.getHours())}:${pad(roundedStart.getMinutes())}`);
        setEndDate(`${roundedNow.getFullYear()}-${pad(roundedNow.getMonth() + 1)}-${pad(roundedNow.getDate())}`);
        setEndTime(`${pad(roundedNow.getHours())}:${pad(roundedNow.getMinutes())}`);

        // Initialize axis inputs blank so x-axis auto fits on first load
        setXMinStr('');
        setXMaxStr('');
        setYMin('');
        setYMax('');

        fetch(`${apiUrl}/sensors`)
            .then((res) => res.json())
            .then((list) => {
                setSensors(list);
                setSelectedSensors(list.map((s) => s.sensor_id));
                setInitialized(true);
            })
            .catch(console.error);
    }, [apiUrl]);

    // Fetch and group data when filters change
    useEffect(() => {
        if (!initialized) return;
        const params = new URLSearchParams();
        if (selectedSensors.length) params.append('sensor', selectedSensors.join(','));
        if (startDate) params.append('startDate', startDate);
        if (startTime) params.append('startTime', startTime);
        if (endDate) params.append('endDate', endDate);
        if (endTime) params.append('endTime', endTime);

        fetch(`${apiUrl}/sensorData/filter?${params.toString()}`)
            .then((res) => res.json())
            .then((result) => {
                const grouped = {};
                result.forEach((item) => {
                    const ts = new Date(item.local).getTime();
                    if (!grouped[ts]) grouped[ts] = { timestamp: ts };
                    grouped[ts][item.sensor_id] = parseFloat(item.reading);
                });
                setData(Object.values(grouped));
            })
            .catch(console.error);
    }, [startDate, startTime, endDate, endTime, selectedSensors, initialized, apiUrl]);

    // Color palette for sensor lines
    const colors = ['#8884d8', '#82ca9d', '#ffc658', '#ff7300', '#a4de6c'];

    return (
        <div style={{ width: '100%', height: 600 }}>
            <div className="menuContainer">
                {/* Section 1: Sensor Selection */}
                <section className="section1">
                    <h4>Sensor Selection</h4>
                    <div className="sensor-options">
                        {sensors.map((sensor) => (
                            <label key={sensor.sensor_id} style={{ marginRight: 12 }}>
                                <input
                                    type="checkbox"
                                    checked={selectedSensors.includes(sensor.sensor_id)}
                                    onChange={() => toggleSensor(sensor.sensor_id)}
                                />
                                {sensor.name}
                            </label>
                        ))}
                    </div>
                </section>

                {/* Section 2: Date & Range */}
                <section className="section2">
                    <h4>Date and Range</h4>
                    <div className="range-selector" style={{ display: 'flex', gap: '24px' }}>
                        <div>
                            <label>
                                Start Date:
                                <input
                                    type="date"
                                    value={startDate}
                                    onChange={(e) => setStartDate(e.target.value)}
                                />
                            </label>
                            <label style={{ marginLeft: 12 }}>
                                Start Time:
                                <input
                                    type="time"
                                    step="300"
                                    value={startTime}
                                    onChange={(e) => setStartTime(e.target.value)}
                                />
                            </label>
                        </div>
                        <div>
                            <label>
                                End Date:
                                <input
                                    type="date"
                                    value={endDate}
                                    onChange={(e) => setEndDate(e.target.value)}
                                />
                            </label>
                            <label style={{ marginLeft: 12 }}>
                                End Time:
                                <input
                                    type="time"
                                    step="300"
                                    value={endTime}
                                    onChange={(e) => setEndTime(e.target.value)}
                                />
                            </label>
                        </div>
                    </div>
                </section>

                {/* Section 3: Data Options */}
                <section className="section3">
                    <h4>Data Options</h4>
                    <div style={{ marginBottom: 8 }}>
                        <label style={{ marginRight: 12 }}>
                            X Min:
                            <input
                                type="time"
                                step="1"
                                value={xMinStr}
                                onChange={(e) => setXMinStr(e.target.value)}
                                style={{ marginLeft: 4 }}
                            />
                        </label>
                        <label>
                            X Max:
                            <input
                                type="time"
                                step="1"
                                value={xMaxStr}
                                onChange={(e) => setXMaxStr(e.target.value)}
                                style={{ marginLeft: 4 }}
                            />
                        </label>
                    </div>
                    <div>
                        <label style={{ marginRight: 12 }}>
                            Y Min:
                            <input
                                type="number"
                                step="0.1"
                                value={yMin}
                                onChange={(e) => setYMin(e.target.value)}
                                style={{ marginLeft: 4, width: 80 }}
                            />
                        </label>
                        <label>
                            Y Max:
                            <input
                                type="number"
                                step="0.1"
                                value={yMax}
                                onChange={(e) => setYMax(e.target.value)}
                                style={{ marginLeft: 4, width: 80 }}
                            />
                        </label>
                    </div>
                    <button onClick={downloadCSV} style={{ marginTop: 12 }}>
                        Download as CSV
                    </button>
                </section>
            </div>

            {/* Chart */}
            <ResponsiveContainer width="100%" height="100%">
                <LineChart data={data} margin={{ top: 20, right: 10, bottom: 80, left: 10 }}>
                    <CartesianGrid strokeDasharray="3 3" />
                    <XAxis
                        dataKey="timestamp"
                        type="number"
                        domain={[
                            xMinStr ? new Date(`${startDate}T${xMinStr}`).getTime() : 'auto',
                            xMaxStr ? new Date(`${endDate}T${xMaxStr}`).getTime() : 'auto',
                        ]}
                        tickFormatter={(ms) =>
                            new Date(ms).toLocaleTimeString('en-GB', {
                                hour12: false,
                                hour: '2-digit',
                                minute: '2-digit',
                                second: '2-digit',
                            })
                        }
                        label={{ value: 'Local Time', position: 'bottom', offset: 20, style: { fontWeight: 'bold' } }}
                    />
                    <YAxis
                        domain={[
                            yMin !== '' ? Number(yMin) : 'auto',
                            yMax !== '' ? Number(yMax) : 'auto',
                        ]}
                        label={{ value: 'mag/arcsec²', angle: -90, position: 'insideLeft', dy: 50, style: { fontWeight: 'bold' } }}
                    />
                    <Tooltip content={<CustomTooltip />} />
                    <Legend verticalAlign="top" />
                    {sensors
                        .filter((s) => selectedSensors.includes(s.sensor_id))
                        .map((sensor, idx) => (
                            <Line
                                key={sensor.sensor_id}
                                type="monotone"
                                dataKey={sensor.sensor_id}
                                name={sensor.name}
                                stroke={colors[idx % colors.length]}
                                dot={false}
                            />
                        ))}
                </LineChart>
            </ResponsiveContainer>
        </div>
    );
}

export default LineGraph;
