// ReactChartsLine.js
import React, { useState, useEffect } from 'react';
import {
    LineChart,
    Line,
    XAxis,
    YAxis,
    CartesianGrid,
    Tooltip,
    Legend,
    ResponsiveContainer,
} from 'recharts';
import './ReactChartsLine.css';

// Custom tooltip to display multiple sensor readings with formatted time
const CustomTooltip = ({ active, payload, label }) => {
    if (!active || !payload || payload.length === 0) return null;
    const timeLabel = new Date(label).toLocaleTimeString('en-GB', {
        hour12: false,
        hour: '2-digit',
        minute: '2-digit',
        second: '2-digit',
    });
    return (
        <div style={{ backgroundColor: '#fff', border: '1px solid #ccc', padding: '0.5rem', color: '#000' }}>
            <div style={{ fontWeight: 'bold', marginBottom: 4 }}>Time: {timeLabel}</div>
            {payload.map((entry) => (
                <div key={entry.dataKey} style={{ color: entry.color, fontWeight: 'bold' }}>
                    {entry.name}: {entry.value}
                </div>
            ))}
        </div>
    );
};

function LineGraph() {
    // Core state
    const [data, setData] = useState([]);
    const [sensors, setSensors] = useState([]);
    const [selectedSensors, setSelectedSensors] = useState([]);
    const [initialized, setInitialized] = useState(false);

    // Date/time filters (local, rounded on mount)
    const [startDate, setStartDate] = useState('');
    const [startTime, setStartTime] = useState('');
    const [endDate, setEndDate] = useState('');
    const [endTime, setEndTime] = useState('');

    // Axis step customization
    const [xStep, setXStep] = useState(60);   // minutes
    const [yMin, setYMin] = useState('');     // will be set to 0 initially
    const [yMax, setYMax] = useState('');     // rounded to nearest 5 above max reading
    const [yStep, setYStep] = useState(5);    // units

    const apiUrl = process.env.REACT_APP_API_URL || 'http://localhost:8000';

    // Toggle sensor selection (at least one remains)
    const toggleSensor = (sensorId) => {
        setSelectedSensors((prev) => {
            if (prev.includes(sensorId)) {
                if (prev.length === 1) return prev;
                return prev.filter((id) => id !== sensorId);
            }
            return [...prev, sensorId];
        });
    };

    // Placeholder for CSV download
    const downloadCSV = () => console.log('Download CSV placeholder');

    // On mount: fetch sensors & set default filters rounded to nearest 5 minutes
    useEffect(() => {
        const pad = (n) => String(n).padStart(2, '0');
        const roundToNearest5Min = (date) => {
            const ms = date.getTime();
            const step = 1000 * 60 * 5;
            return new Date(Math.floor(ms / step) * step);
        };

        const now = new Date();
        const roundedNow = roundToNearest5Min(now);
        const startObj = roundToNearest5Min(new Date(roundedNow.getTime() - 12 * 3600 * 1000));

        setStartDate(`${startObj.getFullYear()}-${pad(startObj.getMonth() + 1)}-${pad(startObj.getDate())}`);
        setStartTime(`${pad(startObj.getHours())}:${pad(startObj.getMinutes())}`);
        setEndDate(`${roundedNow.getFullYear()}-${pad(roundedNow.getMonth() + 1)}-${pad(roundedNow.getDate())}`);
        setEndTime(`${pad(roundedNow.getHours())}:${pad(roundedNow.getMinutes())}`);

        fetch(`${apiUrl}/sensors`)
            .then((res) => res.json())
            .then((list) => {
                setSensors(list);
                setSelectedSensors(list.map((s) => s.sensor_id));
                setInitialized(true);
            })
            .catch(console.error);
    }, [apiUrl]);

    // Fetch and process data when filters change
    useEffect(() => {
        if (!initialized) return;
        const params = new URLSearchParams();
        if (selectedSensors.length) params.append('sensor', selectedSensors.join(','));
        if (startDate) params.append('startDate', startDate);
        if (startTime) params.append('startTime', startTime);
        if (endDate) params.append('endDate', endDate);
        if (endTime) params.append('endTime', endTime);

        fetch(`${apiUrl}/sensorData/filter?${params.toString()}`)
            .then((res) => res.json())
            .then((result) => {
                const grouped = {};
                const readingsList = [];
                result.forEach((item) => {
                    const ts = new Date(item.local).getTime();
                    if (!grouped[ts]) grouped[ts] = { timestamp: ts };
                    grouped[ts][item.sensor_id] = parseFloat(item.reading);
                    readingsList.push(parseFloat(item.reading));
                });
                const dataArr = Object.values(grouped);

                // Compute Y domain
                const maxReading = readingsList.length ? Math.max(...readingsList) : 0;
                const roundedMax = Math.ceil(maxReading / 5) * 5;
                setYMin('0');
                setYMax(roundedMax.toString());

                setData(dataArr);
            })
            .catch(console.error);
    }, [startDate, startTime, endDate, endTime, selectedSensors, initialized, apiUrl]);

    // Derive start/end timestamps from filters
    const startTs = startDate && startTime ? new Date(`${startDate}T${startTime}`).getTime() : null;
    const endTs = endDate && endTime ? new Date(`${endDate}T${endTime}`).getTime() : null;

    // Generate ticks based on step settings within filter range
    const xTicks = [];
    if (startTs !== null && endTs !== null) {
        const stepMs = xStep * 60 * 1000;
        for (let t = startTs; t <= endTs; t += stepMs) xTicks.push(t);
    }

    const yMinNum = Number(yMin);
    const yMaxNum = Number(yMax);
    const yTicks = [];
    if (!isNaN(yMinNum) && !isNaN(yMaxNum)) {
        for (let v = yMinNum; v <= yMaxNum; v += yStep) yTicks.push(v);
    }

    // Color palette for sensor lines
    const colors = ['#8884d8', '#82ca9d', '#ffc658', '#ff7300', '#a4de6c'];

    return (
        <div style={{ width: '100%', height: 600 }}>
            <div className="menuContainer">
                {/* Section 1: Sensor Selection */}
                <section className="section1">
                    <h4>Sensor Selection</h4>
                    <div className="sensor-options">
                        {sensors.map((sensor) => (
                            <label key={sensor.sensor_id} style={{ marginRight: 12 }}>
                                <input
                                    type="checkbox"
                                    checked={selectedSensors.includes(sensor.sensor_id)}
                                    onChange={() => toggleSensor(sensor.sensor_id)}
                                />
                                {sensor.name}
                            </label>
                        ))}
                    </div>
                </section>

                {/* Section 2: Date & Range */}
                <section className="section2">
                    <h4>Date and Range</h4>
                    <div className="range-selector" style={{ display: 'flex', gap: '24px' }}>
                        <div>
                            <label>
                                Start Date:
                                <input type="date" value={startDate} onChange={(e) => setStartDate(e.target.value)} />
                            </label>
                            <label style={{ marginLeft: 12 }}>
                                Start Time:
                                <input type="time" step="300" value={startTime} onChange={(e) => setStartTime(e.target.value)} />
                            </label>
                        </div>
                        <div>
                            <label>
                                End Date:
                                <input type="date" value={endDate} onChange={(e) => setEndDate(e.target.value)} />
                            </label>
                            <label style={{ marginLeft: 12 }}>
                                End Time:
                                <input type="time" step="300" value={endTime} onChange={(e) => setEndTime(e.target.value)} />
                            </label>
                        </div>
                    </div>
                </section>

                {/* Section 3: Axis Steps & CSV */}
                <section className="section3">
                    <h4>Data Options</h4>
                    <div style={{ marginBottom: 8, display: 'flex', alignItems: 'center', gap: '24px' }}>
                        <div>
                            <label>
                                X Step:
                                <select value={xStep} onChange={(e) => setXStep(Number(e.target.value))} style={{ marginLeft: 4 }}>
                                    <option value={5}>5 min</option>
                                    <option value={10}>10 min</option>
                                    <option value={30}>30 min</option>
                                    <option value={60}>1 hr</option>
                                </select>
                            </label>
                        </div>
                        <div>
                            <label>
                                Y Min:
                                <input type="number" step="1" value={yMin} onChange={(e) => setYMin(e.target.value)} style={{ marginLeft: 4, width: 60 }} />
                            </label>
                            <label style={{ marginLeft: 12 }}>
                                Y Max:
                                <input type="number" step="1" value={yMax} onChange={(e) => setYMax(e.target.value)} style={{ marginLeft: 4, width: 60 }} />
                            </label>
                        </div>
                        <div>
                            <label>
                                Y Step:
                                <select value={yStep} onChange={(e) => setYStep(Number(e.target.value))} style={{ marginLeft: 4 }}>
                                    <option value={1}>1 unit</option>
                                    <option value={2}>2 units</option>
                                    <option value={5}>5 units</option>
                                    <option value={10}>10 units</option>
                                </select>
                            </label>
                        </div>
                    </div>
                    <button onClick={downloadCSV}>Download as CSV</button>
                </section>
            </div>

            {/* Chart */}
            <ResponsiveContainer width="100%" height="100%">
                <LineChart data={data} margin={{ top: 20, right: 10, bottom: 80, left: 10 }}>
                    <CartesianGrid strokeDasharray="3 3" />
                    <XAxis
                        dataKey="timestamp"
                        type="number"
                        domain={['auto', 'auto']}
                        ticks={xTicks}
                        tickFormatter={(ms) =>
                            new Date(ms).toLocaleTimeString('en-GB', {
                                hour12: false,
                                hour: '2-digit',
                                minute: '2-digit',
                                second: '2-digit',
                            })
                        }
                        label={{ value: 'Local Time', position: 'bottom', offset: 20, style: { fontWeight: 'bold' } }}
                    />
                    <YAxis
                        domain={[yMin !== '' ? Number(yMin) : 'auto', yMax !== '' ? Number(yMax) : 'auto']}
                        ticks={yTicks}
                        label={{ value: 'mag/arcsec²', angle: -90, position: 'insideLeft', dy: 50, style: { fontWeight: 'bold' } }}
                    />
                    <Tooltip content={<CustomTooltip />} />
                    <Legend verticalAlign="top" />
                    {sensors
                        .filter((s) => selectedSensors.includes(s.sensor_id))
                        .map((sensor, idx) => (
                            <Line
                                key={sensor.sensor_id}
                                type="monotone"
                                dataKey={sensor.sensor_id}
                                name={sensor.name}
                                stroke={colors[idx % colors.length]}
                                dot={false}
                            />
                        ))}
                </LineChart>
            </ResponsiveContainer>
        </div>
    );
}

export default LineGraph;
