// ReactChartsLine.js
import React, { useState, useEffect } from 'react';
import {
    LineChart,
    Line,
    XAxis,
    YAxis,
    CartesianGrid,
    Tooltip,
    Legend,
    ResponsiveContainer,
} from 'recharts';

function LineGraph() {
    const [data, setData] = useState([]);

    useEffect(() => {
        const fetchSensorData = async () => {
            try {
                const response = await fetch("http://localhost:8000/sensorData/last12hours");
                const result = await response.json();
                console.log("Fetched sensor data:", result);

                // Sort data chronologically (oldest to newest)
                const sortedData = result.sort(
                    (a, b) => new Date(a.utc) - new Date(b.utc)
                );
                console.log("Sorted sensor data:", sortedData);

                // Map the data to the format needed for the chart.
                const chartData = sortedData.map((item) => ({
                    utc: item.utc,
                    reading: parseFloat(item.reading),
                }));
                console.log("Chart data:", chartData);

                setData(chartData);
            } catch (error) {
                console.error("Error fetching sensor data:", error);
            }
        };

        fetchSensorData();
    }, []);

    return (
        // The parent div needs a fixed height so ResponsiveContainer can compute dimensions.
        <div style={{ width: '100%', height: 400 }}>
            <ResponsiveContainer width="100%" height="100%">
                <LineChart
                    data={data}
                    margin={{
                        top: 5,
                        right: 30,
                        left: 20,
                        bottom: 5,
                    }}
                >
                    <CartesianGrid strokeDasharray="3 3" />
                    {/* XAxis will display the utc values */}
                    <XAxis dataKey="utc" />
                    {/* YAxis will display the sensor reading */}
                    <YAxis />
                    <Tooltip />
                    <Legend />
                    {/* Draw the line for the sensor reading */}
                    <Line type="monotone" dataKey="reading" stroke="#8884d8" activeDot={{ r: 8 }} />
                </LineChart>
            </ResponsiveContainer>
        </div>
    );
}

export default LineGraph;
