import React, { useState, useEffect } from 'react';
import {
    LineChart,
    Line,
    XAxis,
    YAxis,
    CartesianGrid,
    Tooltip,
    Legend,
    ResponsiveContainer,
} from 'recharts';

function LineGraph() {
    const [data, setData] = useState([]);

    // Fetch sensor data from the server
    useEffect(() => {
        const fetchSensorData = async () => {
            try {
                const response = await fetch("http://localhost:8000/sensorData/last12hours");
                const result = await response.json();
                console.log("Fetched sensor data:", result);

                // Sort data chronologically (oldest to newest)
                const sortedData = result.sort(
                    (a, b) => new Date(a.utc) - new Date(b.utc)
                );
                console.log("Sorted data:", sortedData);

                // Map data into a format suitable for our chart.
                // We assume each object has a "utc" (timestamp) and "reading" (string) property.
                const chartData = sortedData.map((point) => ({
                    utc: point.utc,
                    reading: parseFloat(point.reading),
                }));
                console.log("Chart data:", chartData);

                setData(chartData);
            } catch (error) {
                console.error("Error fetching sensor data:", error);
            }
        };

        fetchSensorData();
    }, []);

    return (
        <ResponsiveContainer width="100%" height="100%">
            <LineChart
                width={500}
                height={300}
                data={data}
                margin={{
                    top: 5,
                    right: 30,
                    left: 20,
                    bottom: 5,
                }}
            >
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="utc" />
                <YAxis />
                <Tooltip />
                <Legend />
                <Line type="monotone" dataKey="reading" stroke="#8884d8" activeDot={{ r: 8 }} />
            </LineChart>
        </ResponsiveContainer>
    );
}

export default LineGraph;
