// ReactChartsLine.js
import React, { useState, useEffect } from 'react';
import {
    LineChart,
    Line,
    XAxis,
    YAxis,
    CartesianGrid,
    Tooltip,
    Legend,
    ResponsiveContainer,
} from 'recharts';
import './ReactChartsLine.css';

function LineGraph() {
    // Data and sensor selection state
    const [data, setData] = useState([]);
    const [selectedSensors, setSelectedSensors] = useState([]);
    const [sensors, setSensors] = useState([]); // Available sensors list

    // State for the date range selection
    const [startDate, setStartDate] = useState('');
    const [startTime, setStartTime] = useState('');
    const [endDate, setEndDate] = useState('');
    const [endTime, setEndTime] = useState('');

    // Toggle function for sensor checkboxes (allows multiple selections)
    const toggleSensor = (sensorId) => {
        setSelectedSensors((prevSelected) =>
            prevSelected.includes(sensorId)
                ? prevSelected.filter((s) => s !== sensorId)
                : [...prevSelected, sensorId]
        );
    };

    // Placeholder function for CSV download
    const downloadCSV = () => {
        console.log("CSV download initiated (placeholder)");
    };

    // Fetch sensor data for the chart on component mount
    useEffect(() => {
        const apiUrl = process.env.REACT_APP_API_URL || 'http://localhost:8000';
        const fetchUrl = `${apiUrl}/sensorData/last12hours`;

        const fetchSensorData = async () => {
            try {
                console.log("Fetching sensor data from:", fetchUrl);
                const response = await fetch(fetchUrl, {
                    headers: { 'Accept': 'application/json' }
                });
                if (!response.ok) {
                    throw new Error(`HTTP error! status: ${response.status}`);
                }
                const result = await response.json();
                console.log("Fetched sensor data:", result);

                // Sort data by UTC timestamp (oldest first)
                const sortedData = result.sort(
                    (a, b) => new Date(a.local) - new Date(b.local)
                );
                console.log("Sorted sensor data:", sortedData);

                // Map API data to chart format using keys 'time' and 'reading'
                const chartData = sortedData.map((item) => ({
                    time: new Date(item.local).toLocaleString('en-GB', {
                        hour12: false,
                        hour: '2-digit',
                        minute: '2-digit',
                        second: '2-digit'
                    }),
                    reading: parseFloat(item.reading)
                }));

                console.log("Chart data:", chartData);
                setData(chartData);
            } catch (error) {
                console.error("Error fetching sensor data:", error);
            }
        };

        fetchSensorData();
    }, []);

    // Fetch available sensors for the selection options on component mount
    useEffect(() => {
        const apiUrl = process.env.REACT_APP_API_URL || 'http://localhost:8000';
        const sensorsUrl = `${apiUrl}/sensors`;

        const fetchSensors = async () => {
            try {
                console.log("Fetching sensors from:", sensorsUrl);
                const response = await fetch(sensorsUrl, {
                    headers: { 'Accept': 'application/json' }
                });
                if (!response.ok) {
                    throw new Error(`HTTP error! status: ${response.status}`);
                }
                const sensorList = await response.json();
                console.log("Fetched sensor list:", sensorList);

                setSensors(sensorList);
            } catch (error) {
                console.error("Error fetching sensors:", error);
            }
        };

        fetchSensors();
    }, []);

    // Set default date/time values on mount: 12 hours ago to now, rounded to the nearest 5 minutes.
    useEffect(() => {
        const roundToNearest5Min = (date) => {
            // Calculate number of milliseconds in 5 minutes
            const step = 1000 * 60 * 5;
            return new Date(Math.floor(date.getTime() / step) * step);
        };

        const now = new Date();
        const end = roundToNearest5Min(now);
        const start = roundToNearest5Min(new Date(now.getTime() - 12 * 60 * 60 * 1000));

        // Format date as YYYY-MM-DD and time as HH:MM
        const formatDate = (date) => date.toISOString().slice(0, 10);
        const formatTime = (date) => date.toISOString().slice(11, 16);

        setEndDate(formatDate(end));
        setEndTime(formatTime(end));
        setStartDate(formatDate(start));
        setStartTime(formatTime(start));
    }, []);

    return (
        <div style={{ width: '100%', height: 400 }}>
            <div className="menuContainer">
                {/* Section 1: Sensor Selection */}
                <section className="section1">
                    <h5>Sensor Selection</h5>
                    <div className="sensor-options">
                        {sensors.map((sensor) => (
                            <label key={sensor.sensor_id}>
                                <input
                                    type="checkbox"
                                    value={sensor.sensor_id}
                                    checked={selectedSensors.includes(sensor.sensor_id)}
                                    onChange={() => toggleSensor(sensor.sensor_id)}
                                />
                                {sensor.name}
                            </label>
                        ))}
                    </div>
                </section>

                {/* Section 2: Date and Range */}
                <section className="section2">
                    <h5>Date and Range</h5>
                    <div className="range-selector">
                        <div className="range-group">
                            <label>
                                Start Date:
                                <input
                                    type="date"
                                    value={startDate}
                                    onChange={(e) => setStartDate(e.target.value)}
                                />
                            </label>
                            <label>
                                Start Time:
                                <input
                                    type="time"
                                    step="300"  // 300 seconds = 5 minutes
                                    value={startTime}
                                    onChange={(e) => setStartTime(e.target.value)}
                                />
                            </label>
                        </div>
                        <div className="range-group">
                            <label>
                                End Date:
                                <input
                                    type="date"
                                    value={endDate}
                                    onChange={(e) => setEndDate(e.target.value)}
                                />
                            </label>
                            <label>
                                End Time:
                                <input
                                    type="time"
                                    step="300"
                                    value={endTime}
                                    onChange={(e) => setEndTime(e.target.value)}
                                />
                            </label>
                        </div>
                    </div>
                </section>

                {/* Section 3: Data Options */}
                <section className="section3">
                    <h5>Data Options</h5>
                    <button onClick={downloadCSV}>Download as CSV</button>
                </section>
            </div>

            {/* Chart */}
            <ResponsiveContainer width="100%" height="100%">
                <LineChart
                    data={data}
                    margin={{ top: 5, right: 30, left: 20, bottom: 5 }}
                >
                    <CartesianGrid strokeDasharray="3 3" />
                    <XAxis dataKey="time" />
                    <YAxis />
                    <Tooltip />
                    <Legend />
                    <Line type="monotone" dataKey="reading" stroke="#8884d8" activeDot={{ r: 8 }} />
                </LineChart>
            </ResponsiveContainer>
        </div>
    );
}

export default LineGraph;
