import React, { useState, useEffect } from 'react';
import {
    LineChart,
    Line,
    XAxis,
    YAxis,
    CartesianGrid,
    Tooltip,
    Legend,
    ResponsiveContainer,
} from 'recharts';
import './ReactChartsLine.css';

// Custom tooltip to display multiple sensor readings with formatted time
const CustomTooltip = ({ active, payload, label }) => {
    if (!active || !payload || !payload.length) return null;
    const fmt = new Intl.DateTimeFormat('en-US', {
        hour12: false,
        month: '2-digit',
        day: '2-digit',
        hour: '2-digit',
        minute: '2-digit',
    });
    const timeLabel = fmt.format(new Date(label));
    return (
        <div style={{ background: '#fff', border: '1px solid #ccc', padding: 8 }}>
            <div style={{ fontWeight: 'bold', marginBottom: 4 }}>Time: {timeLabel}</div>
            {payload.map((entry) => (
                <div key={entry.dataKey} style={{ color: entry.color }}>
                    <strong>{entry.name}:</strong> {entry.value}
                </div>
            ))}
        </div>
    );
};

export default function LineGraph() {
    const [data, setData] = useState([]);       // pivoted for chart
    const [rawData, setRawData] = useState([]); // for CSV download
    const [sensors, setSensors] = useState([]);
    const [selectedSensors, setSelectedSensors] = useState([]);
    const [initialized, setInitialized] = useState(false);

    // date/time filters
    const [startDate, setStartDate] = useState('');
    const [startTime, setStartTime] = useState('');
    const [endDate, setEndDate] = useState('');
    const [endTime, setEndTime] = useState('');

    // y‑axis
    const [yMin, setYMin] = useState('0');
    const [yMax, setYMax] = useState('0');
    const [yStep, setYStep] = useState(5);

    const apiUrl = process.env.REACT_APP_API_URL || 'http://localhost:8000';

    // ensure at least one sensor stays checked
    const toggleSensor = (id) =>
        setSelectedSensors((prev) => {
            if (prev.includes(id)) {
                return prev.length === 1 ? prev : prev.filter(x => x !== id);
            }
            return [...prev, id];
        });

    // build & download CSV
    const downloadCSV = () => {
        if (!rawData.length) return;
        const idToName = Object.fromEntries(sensors.map(s => [s.sensor_id, s.name]));
        const headers = Object.keys(rawData[0]).map(h => h === 'sensor_id' ? 'sensor_name' : h);
        const rows = rawData.map(r =>
            headers.map(h =>
                h === 'sensor_name'
                    ? (idToName[r.sensor_id] ?? r.sensor_id)
                    : r[h]
            )
        );
        const csv = [headers, ...rows]
            .map(r => r.map(c => `"${c}"`).join(','))
            .join('\r\n');
        const blob = new Blob([csv], { type: 'text/csv' });
        const url = URL.createObjectURL(blob);
        const a = document.createElement('a');
        a.href = url; a.download = 'Utah-skyscope-data.csv';
        document.body.appendChild(a);
        a.click();
        a.remove();
        URL.revokeObjectURL(url);
    };

    // on‑mount: round to 5 min, set filters, fetch sensor list
    useEffect(() => {
        const pad = n => String(n).padStart(2, '0');
        const round5 = d => {
            const ms = d.getTime(), step = 5 * 60 * 1000;
            return new Date(Math.floor(ms/step)*step);
        };
        const now = round5(new Date());
        const ago12 = new Date(now.getTime() - 12*3600*1000);

        setStartDate(`${ago12.getFullYear()}-${pad(ago12.getMonth()+1)}-${pad(ago12.getDate())}`);
        setStartTime(`${pad(ago12.getHours())}:${pad(ago12.getMinutes())}`);
        setEndDate(`${now.getFullYear()}-${pad(now.getMonth()+1)}-${pad(now.getDate())}`);
        setEndTime(`${pad(now.getHours())}:${pad(now.getMinutes())}`);

        fetch(`${apiUrl}/sensors`)
            .then(r => r.json())
            .then(list => {
                setSensors(list);
                setSelectedSensors(list.map(s => s.sensor_id));
                setInitialized(true);
            })
            .catch(console.error);
    }, [apiUrl]);

    // fetch & pivot for chart on any filter change
    useEffect(() => {
        if (!initialized) return;
        const fetchAndPivot = () => {
            const params = new URLSearchParams();
            if (selectedSensors.length) params.set('sensor', selectedSensors.join(','));
            if (startDate) params.set('startDate', startDate);
            if (startTime) params.set('startTime', startTime);
            if (endDate) params.set('endDate', endDate);
            if (endTime) params.set('endTime', endTime);

            fetch(`${apiUrl}/sensorData/filter?${params}`)
                .then(r => r.json())
                .then(rows => {
                    setRawData(rows);
                    // pivot rows into one object per timestamp
                    const map = {};
                    const allReadings = [];
                    rows.forEach(({ sensor_id, local, reading }) => {
                        const ts = new Date(local).getTime();
                        map[ts] = map[ts] || { timestamp: ts };
                        map[ts][sensor_id] = parseFloat(reading);
                        allReadings.push(parseFloat(reading));
                    });
                    const chartArr = Object.values(map).sort((a,b) => a.timestamp - b.timestamp);
                    setData(chartArr);

                    // recalc Y domain
                    const maxR = allReadings.length ? Math.max(...allReadings) : 0;
                    const ceilMax = Math.ceil(maxR / yStep) * yStep;
                    setYMin('0');
                    setYMax(String(ceilMax));
                })
                .catch(console.error);
        };

        fetchAndPivot();
        const iv = setInterval(fetchAndPivot, 60_000);
        return () => clearInterval(iv);
    }, [
        apiUrl, initialized,
        startDate, startTime, endDate, endTime,
        selectedSensors, yStep
    ]);

    // compute tick domains
    const startTs = startDate && startTime
        ? new Date(`${startDate}T${startTime}`).getTime()
        : 0;
    const endTs = endDate && endTime
        ? new Date(`${endDate}T${endTime}`).getTime()
        : Date.now();

    const xTicks = [];
    let ft = new Date(startTs);
    ft.setMinutes(0,0,0);
    if (ft.getTime() < startTs) ft = new Date(ft.getTime() + 3600_000);
    for (let t = ft.getTime(); t <= endTs; t += 3600_000) xTicks.push(t);

    const yMinNum = Number(yMin), yMaxNum = Number(yMax);
    const yTicks = [];
    for (let v = yMinNum; v <= yMaxNum; v += yStep) yTicks.push(v);

    const colors = ['#8884d8','#82ca9d','#ffc658','#ff7300','#a4de6c'];

    return (
        <div style={{ width: '100%', height: 600 }}>
            <div className="menuContainer">
                <section className="section1">
                    <h4>Sensor Selection</h4>
                    <div className="sensor-options">
                        {sensors.map(s => (
                            <label key={s.sensor_id} style={{ marginRight: 12 }}>
                                <input
                                    type="checkbox"
                                    checked={selectedSensors.includes(s.sensor_id)}
                                    onChange={() => toggleSensor(s.sensor_id)}
                                />
                                {s.name}
                            </label>
                        ))}
                    </div>
                </section>
                <section className="section2">
                    <h4>Date & Range</h4>
                    <div style={{ display:'flex', gap:24, justifyContent:'center' }}>
                        <div>
                            <label>Start Date<br/>
                                <input type="date" value={startDate}
                                       onChange={e => setStartDate(e.target.value)} />
                            </label><br/>
                            <label>Start Time<br/>
                                <input type="time" step="300" value={startTime}
                                       onChange={e => setStartTime(e.target.value)} />
                            </label>
                        </div>
                        <div>
                            <label>End Date<br/>
                                <input type="date" value={endDate}
                                       onChange={e => setEndDate(e.target.value)} />
                            </label><br/>
                            <label>End Time<br/>
                                <input type="time" step="300" value={endTime}
                                       onChange={e => setEndTime(e.target.value)} />
                            </label>
                        </div>
                    </div>
                </section>
                <section className="section3">
                    <h4>Data Options</h4>
                    <div style={{ display:'flex', alignItems:'center', gap:24, marginBottom:8, justifyContent:'center' }}>
                        <div>
                            <label>Y Min<br/>
                                <input
                                    type="number"
                                    value={yMin}
                                    onChange={e => setYMin(e.target.value)}
                                    style={{ width:60 }}
                                />
                            </label>
                        </div>
                        <div>
                            <label>Y Max<br/>
                                <input
                                    type="number"
                                    value={yMax}
                                    onChange={e => setYMax(e.target.value)}
                                    style={{ width:60 }}
                                />
                            </label>
                        </div>
                        <div>
                            <label>Y Step<br/>
                                <select
                                    value={yStep}
                                    onChange={e => setYStep(Number(e.target.value))}
                                >
                                    <option value={1}>1 unit</option>
                                    <option value={2}>2 units</option>
                                    <option value={5}>5 units</option>
                                    <option value={10}>10 units</option>
                                </select>
                            </label>
                        </div>
                    </div>
                    <button onClick={downloadCSV}>Download as CSV</button>
                </section>
            </div>

            <ResponsiveContainer width="100%" height="100%">
                <LineChart
                    data={data}
                    margin={{ top:20, right:10, bottom:80, left:10 }}
                >
                    <CartesianGrid strokeDasharray="3 3" />
                    <XAxis
                        dataKey="timestamp"
                        type="number"
                        domain={[startTs, endTs]}
                        ticks={xTicks}
                        tickFormatter={ms =>
                            new Date(ms).toLocaleTimeString('en-GB', {
                                hour12: false,
                                hour:'2-digit', minute:'2-digit',
                            })
                        }
                        label={{
                            value: 'Local Time',
                            position: 'bottom',
                            offset: 20,
                            style: { fontWeight:'bold' }
                        }}
                    />
                    <YAxis
                        domain={[yMinNum, yMaxNum]}
                        ticks={yTicks}
                        label={{
                            value: 'mag/arcsec²',
                            angle: -90,
                            position: 'insideLeft',
                            dy: 50,
                            style: { fontWeight:'bold' }
                        }}
                    />
                    <Tooltip content={<CustomTooltip />} />
                    <Legend verticalAlign="top" />
                    {sensors
                        .filter(s => selectedSensors.includes(s.sensor_id))
                        .map((s, i) => (
                            <Line
                                key={s.sensor_id}
                                type="monotone"
                                dataKey={s.sensor_id}
                                name={s.name}
                                stroke={colors[i % colors.length]}
                                dot={false}
                            />
                        ))}
                </LineChart>
            </ResponsiveContainer>
        </div>
    );
}
