// ReactChartsLine.js
import React, { useState, useEffect } from 'react';
import {
    LineChart,
    Line,
    XAxis,
    YAxis,
    CartesianGrid,
    Tooltip,
    Legend,
    ResponsiveContainer,
} from 'recharts';
import './ReactChartsLine.css';

function LineGraph() {
    // Data and sensor selection state
    const [data, setData] = useState([]);
    const [selectedSensors, setSelectedSensors] = useState([]);
    const [sensors, setSensors] = useState([]); // Available sensors list

    // State for the date range selection (local time)
    const [startDate, setStartDate] = useState('');
    const [startTime, setStartTime] = useState('');
    const [endDate, setEndDate] = useState('');
    const [endTime, setEndTime] = useState('');

    // Flag to indicate that defaults have been set
    const [initialized, setInitialized] = useState(false);

    const apiUrl = process.env.REACT_APP_API_URL || 'http://localhost:8000';

    // Toggle function for sensor checkboxes (ensuring at least one sensor remains selected)
    const toggleSensor = (sensorId) => {
        setSelectedSensors((prevSelected) => {
            // If sensorId is already selected, check if it is the only one selected.
            if (prevSelected.includes(sensorId)) {
                if (prevSelected.length === 1) {
                    console.warn("At least one sensor must be selected.");
                    return prevSelected;
                } else {
                    return prevSelected.filter((s) => s !== sensorId);
                }
            } else {
                // Otherwise, add it to the selection.
                return [...prevSelected, sensorId];
            }
        });
    };

    // Placeholder function for CSV download
    const downloadCSV = () => {
        console.log("CSV download initiated (placeholder)");
    };

    // Function to update graph data based on filters
    const updateGraph = async () => {
        try {
            // Build query parameters using URLSearchParams
            const params = new URLSearchParams();
            if (selectedSensors.length > 0) {
                // Join multiple sensor IDs with commas
                params.append('sensor', selectedSensors.join(','));
            }
            if (startDate) params.append('startDate', startDate);
            if (startTime) params.append('startTime', startTime);
            if (endDate) params.append('endDate', endDate);
            if (endTime) params.append('endTime', endTime);

            const fetchUrl = `${apiUrl}/sensorData/filter?${params.toString()}`;
            console.log("Fetching filtered sensor data from:", fetchUrl);
            const response = await fetch(fetchUrl, {
                headers: { 'Accept': 'application/json' }
            });
            if (!response.ok) {
                throw new Error(`HTTP error! status: ${response.status}`);
            }
            const result = await response.json();
            console.log("Fetched filtered sensor data:", result);

            // Sort the data by local timestamp (oldest first)
            const sortedData = result.sort(
                (a, b) => new Date(a.local) - new Date(b.local)
            );

            // Map API data to chart format with 'time' and 'reading'
            const chartData = sortedData.map((item) => ({
                time: new Date(item.local).toLocaleString('en-GB', {
                    hour12: false,
                    hour: '2-digit',
                    minute: '2-digit',
                    second: '2-digit'
                }),
                reading: parseFloat(item.reading)
            }));
            console.log("Updated chart data:", chartData);
            setData(chartData);
        } catch (error) {
            console.error("Error fetching filtered sensor data:", error);
        }
    };

    // Initialization effect (runs once on mount)
    // This effect sets default date/time values, fetches sensor list,
    // selects all sensors by default, and sets the "initialized" flag.
    useEffect(() => {
        // Helper functions for formatting date and time in local format
        const pad = (num) => num.toString().padStart(2, '0');
        const formatDateLocal = (date) =>
            `${date.getFullYear()}-${pad(date.getMonth() + 1)}-${pad(date.getDate())}`;
        const formatTimeLocal = (date) =>
            `${pad(date.getHours())}:${pad(date.getMinutes())}`;
        const roundToNearest5Min = (date) => {
            const step = 1000 * 60 * 5; // 5 minutes in milliseconds
            return new Date(Math.floor(date.getTime() / step) * step);
        };

        // Set default date/time (last 12 hours rounded to the nearest 5 minutes)
        const now = new Date();
        const end = roundToNearest5Min(now);
        const start = roundToNearest5Min(new Date(now.getTime() - 12 * 60 * 60 * 1000));
        setEndDate(formatDateLocal(end));
        setEndTime(formatTimeLocal(end));
        setStartDate(formatDateLocal(start));
        setStartTime(formatTimeLocal(start));

        // Fetch sensors from the API
        const sensorsUrl = `${apiUrl}/sensors`;
        const fetchSensors = async () => {
            try {
                console.log("Fetching sensors from:", sensorsUrl);
                const response = await fetch(sensorsUrl, {
                    headers: { 'Accept': 'application/json' }
                });
                if (!response.ok) {
                    throw new Error(`HTTP error! status: ${response.status}`);
                }
                const sensorList = await response.json();
                console.log("Fetched sensor list:", sensorList);
                setSensors(sensorList);
                // Select all sensors by default
                if (sensorList && sensorList.length > 0) {
                    const sensorIds = sensorList.map((sensor) => sensor.sensor_id);
                    setSelectedSensors(sensorIds);
                }
            } catch (error) {
                console.error("Error fetching sensors:", error);
            }
        };

        // Fetch sensors then mark initialization as complete.
        fetchSensors().then(() => {
            setInitialized(true);
        });
    }, [apiUrl]);

    // Effect to update the graph whenever filter values change, but only after initialization.
    useEffect(() => {
        if (initialized) {
            updateGraph();
        }
    }, [startDate, startTime, endDate, endTime, selectedSensors, initialized]);

    return (
        <div style={{ width: '100%', height: 400 }}>
            <div className="menuContainer">
                {/* Section 1: Sensor Selection */}
                <section className="section1">
                    <h4>Sensor Selection</h4>
                    <div className="sensor-options">
                        {sensors.map((sensor) => (
                            <label key={sensor.sensor_id}>
                                <input
                                    type="checkbox"
                                    value={sensor.sensor_id}
                                    checked={selectedSensors.includes(sensor.sensor_id)}
                                    onChange={() => toggleSensor(sensor.sensor_id)}
                                />
                                {sensor.name}
                            </label>
                        ))}
                    </div>
                </section>

                {/* Section 2: Date and Range */}
                <section className="section2">
                    <h4>Date and Range</h4>
                    <div className="range-selector">
                        <div className="range-group">
                            <label>
                                Start Date:
                                <input
                                    type="date"
                                    value={startDate}
                                    onChange={(e) => setStartDate(e.target.value)}
                                />
                            </label>
                            <br/>
                            <label>
                                Start Time:
                                <input
                                    type="time"
                                    step="300" // 300 seconds = 5 minutes
                                    value={startTime}
                                    onChange={(e) => setStartTime(e.target.value)}
                                />
                            </label>
                            <br/>
                        </div>
                        <div className="range-group">
                            <label>
                                End Date:
                                <input
                                    type="date"
                                    value={endDate}
                                    onChange={(e) => setEndDate(e.target.value)}
                                />
                            </label>
                        <br/>
                            <label>
                                End Time:
                                <input
                                    type="time"
                                    step="300"
                                    value={endTime}
                                    onChange={(e) => setEndTime(e.target.value)}
                                />
                            </label>
                        </div>
                    </div>
                </section>

                {/* Section 3: Data Options */}
                <section className="section3">
                    <h4>Data Options</h4>
                    <button onClick={downloadCSV}>Download as CSV</button>
                    {/*<button onClick={updateGraph}>Apply Filters</button>*/}
                </section>
            </div>

            {/* Chart */}
            <ResponsiveContainer width="100%" height={500}>
                <LineChart data={data} margin={{ top: 5, right: 30, left: 20, bottom: 5 }}>
                    <CartesianGrid strokeDasharray="3 3" />
                    <XAxis dataKey="time"
                           height={70}
                           label={{
                               value: "Local Time",

                               style : {
                                   fontWeight: 'bold',
                               }
                    }}
                    />
                    <YAxis label={{
                        value: "mag/arcsec²",
                        angle: -90,
                        position: 'insideLeft',
                        dy: 50,
                        style: {fontWeight: 'bold'},
                    }} />
                    <Tooltip
                        labelFormatter={label => `Time: ${label}`}
                        labelStyle={color= '#000'}
                        formatter={(value, name) => [value, 'mag/arcsec²']}
                        itemStyle={{ fontWeight: 'bold' }} />
                    <Legend verticalAlign='top'
                            height={25}
                            formatter={() => 'Sensor Name'}
                    />
                    <Line type="monotone" dataKey="reading" stroke="#8884d8" activeDot={{ r: 8 }} />
                </LineChart>
            </ResponsiveContainer>
        </div>
    );
}

export default LineGraph;
